import { Button, Card, DatePicker, Divider, Input, Progress, Slider, Spin, Switch, Imag, List } from "antd";
import React, { useState } from "react";
import { utils } from "ethers";
import { SyncOutlined } from "@ant-design/icons";
import { useEffect } from "react";

import { Address, Balance, Events } from "../components";

export default function MintUI({
  address,
  mainnetProvider,
  localProvider,
  yourMainnetBalance,
  yourLocalBalance,
  price,
  tx,
  readContracts,
  writeContracts,
}) {

  const [yourCollectibles, setYourCollectibles] = useState();
  const [color, setColor] = useState("ADD8E6");


  useEffect(() => {
    const updateYourCollectibles = async () => {
      const collectibleUpdate = [];
      try {
        const balance = await readContracts.PixelNFT.getNFTCount();
        console.log("balance of ", parseInt(balance));
        for (let tokenIndex = 0; tokenIndex < balance; tokenIndex++) {

          const tokenURI = await readContracts.PixelNFT.tokenURI(tokenIndex);
          const ownerAddress = await readContracts.PixelNFT.ownerOf(tokenIndex);

          const jsonManifestString = atob(tokenURI.substring(29));

          try {
            const jsonManifest = JSON.parse(jsonManifestString);
            collectibleUpdate.push({ id: tokenIndex, uri: tokenURI, owner: ownerAddress, ...jsonManifest });
          } catch (e) {
            console.log(e);
          }
        } 
      }
      catch (e) {
        console.log(e);
      }
      setYourCollectibles(collectibleUpdate.reverse());
    };
    updateYourCollectibles();
  }, [address, yourMainnetBalance, yourLocalBalance]);

  const fallbackImg =
    "data:image/png;base64,";
  return (
    <div>
      {/*
        ‚öôÔ∏è Here is an example UI that displays and sets the purpose in your smart contract:
      */}
      <div style={{ border: "1px solid #cccccc", padding: 16, width: 400, margin: "auto", marginTop: 64 }}>
        <h2>Mint Pixel NFT:</h2>
        <div style={{ backgroundColor: "#" + color, height: 200, width: 200, margin: "auto" }}>
        </div>
        <Divider />
        <div style={{ margin: 8 }}>
          <Input
            onChange={e => {
              setColor(e.target.value);
            }}
          />
          <Button
            style={{ marginTop: 8 }}
            onClick={async () => {
              /* look how you call setPurpose on your contract: */
              /* notice how you pass a call back for tx updates too */
              const priceRightNow = await readContracts.PixelNFT.price();
              const result = tx(writeContracts.PixelNFT.mintItem(color, { value: priceRightNow }), update => {
                console.log("üì° Transaction Update:", update);
                if (update && (update.status === "confirmed" || update.status === 1)) {
                  console.log(" üçæ Transaction " + update.hash + " finished!");
                  console.log(
                    " ‚õΩÔ∏è " +
                    update.gasUsed +
                    "/" +
                    (update.gasLimit || update.gas) +
                    " @ " +
                    parseFloat(update.gasPrice) / 1000000000 +
                    " gwei",
                  );
                }
              });
              console.log("awaiting metamask/web3 confirm result...", result);
              console.log(await result);
            }}
          >
            mint now for 0.001 ETH
          </Button>
        </div>
        <Divider />
        Your Address:
        <Address address={address} ensProvider={mainnetProvider} fontSize={16} />
        <Divider />
        <h2>Your Balance: {yourLocalBalance ? utils.formatEther(yourLocalBalance) : "..."}</h2>
        <div>OR</div>
        <Balance address={address} provider={localProvider} price={price} />
        {/* <Divider /> */}
        {/* <h2>Your Balance: {yourMainnetBalance ? utils.formatEther(yourMainnetBalance) : "..."}</h2>
        <div>OR</div>
        <Balance address={address} provider={mainnetProvider} price={price} />
        <Divider /> */}
      </div>
      <div style={{ width: 820, margin: "auto", paddingBottom: 256 }}>
              <List
                bordered
                dataSource={yourCollectibles}
                renderItem={item => {
                  const id = item.id;

                  return (
                    <List.Item key={id + "_" + item.uri + "_" + item.owner}>
                      <Card
                        title={
                          <div>
                            <span style={{ fontSize: 18, marginRight: 8 }}>{item.name}</span>
                          </div>
                        }
                      >
                        <img src={item.image} alt={"Pixel #" + id} />
                        <div>{item.description + "\nPixel #" + id}</div>
                      </Card>

                      <div>
                        owner:{" "}
                        <Address
                          address={item.owner}
                          ensProvider={mainnetProvider}
                          fontSize={16}
                        />
                      </div>
                    </List.Item>
                  );
                }}
              />
            </div>
    </div>
  );
}
